INSERT INTO `depoimentos` (`id`, `nome_autor`, `depoimento`, `ano_id`) VALUES
(1, 'Lucas Rafael - Aluno do Curso de Engenharia de Software.', 'Sou aluno de Engenharia de Software, já participei de duas edições dos Encontros Universitários, e em um deles, apresentando mais de um trabalho, apesar da correria que é, posso dizer que as experiências que tive foram ótimas, experiência essa, que pode me proporcionar o contato com outros alunos, com os professores, a troca de conhecimento e todas as lembranças que vou levar após me formar.', 2),
(2, 'Álisson Maia - Aluno do curso de Engenharia de Software', 'Me chamo Álisson, aluno de Engenharia de Software, e tenho a dizer que os Encontros Universitários são uma experiência única, além de não ser nada muito complicado. Você só tem a ganhar.', 2);

