--
-- Estrutura para tabela `editais`
--

CREATE TABLE `editais` (
  `id` int NOT NULL,
  `nome` varchar(100) NOT NULL,
  `descricao` longtext,
  `tipo` varchar(100) NOT NULL,
  `arquivo` varchar(200) DEFAULT NULL,
  `link` varchar(250) DEFAULT NULL,
  `ano_id` int DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3;

--
-- Despejando dados para a tabela `editais`
--

INSERT INTO `editais` (`id`, `nome`, `descricao`, `tipo`, `arquivo`, `link`, `ano_id`) VALUES
(1, 'IV Encontros Universitários da UFC de Russas', '<p><strong>Observações:</strong></p><p>1. os participantes devem estar atentos aos critérios de avaliação dos resumos, pois estes, para serem aceitos para apresentação, deverão obter nota igual ou superior a 6,0 pela comissão científica;</p><p>ou seja, os resumos que forem avaliados com nota abaixo de 6,0 não serão aceitos para apresentação;</p><p>2. o participante (bolsista ou não) poderá escolher seu tipo de envio, Resumo Simples ou Expandido;</p><p>3. caso o participante decida reenviar o seu resumo já com aval do orientador, fazendo uma substituição no sistema, este deverá solicitar novo aval.</p><p>&nbsp;</p><p><a href=\"http://200.129.62.41/encontros/uploads/editais/04-10-2019-16-00-31.pdf\">Edital</a></p><p><a href=\"https://drive.google.com/open?id=17met0uzbMO4x56x6mRSn48FJxn49uP6y\">Edital Assinado</a></p><p><a href=\"https://drive.google.com/open?id=1qaBN9jGQIEXaiQZ_O4px4dKb-8xJFq93\">Aditivo</a></p><p>&nbsp;</p><p>Templates:</p><p>&nbsp;</p><p><a href=\"http://200.129.62.41/encontros/uploads/editais/ModelodeResumo_Simples_EU2019BlindReview.doc\">Template para Resumos Simples (Blind Review).</a></p><p><a href=\"http://200.129.62.41/encontros/uploads/editais/ModelodeResumoSimplesEU2019.doc\">Template para Resumos Simples.</a></p><p><a href=\"http://200.129.62.41/encontros/uploads/editais/templateEU2019_resumo_expandido.docx\">Template para Resumos Expandidos.</a></p><p><a href=\"http://200.129.62.41/encontros/uploads/editais/tex_template_EU.rar\">Template tex para Resumos Expandidos(Atualizado).</a></p><p>&nbsp;</p><p><a href=\"http://200.129.62.41/encontros/uploads/editais/EU2019_Template_Apresentacao.pptx\">Sugestão de template para apresentação oral</a></p><p>&nbsp;</p><p><a href=\"https://drive.google.com/open?id=1-gWyeopkWmufN6witaZ7XO4L6bcxzRds\">Critérios de Avaliação</a></p><p><a href=\"https://drive.google.com/open?id=1mHDYHdxfBWolFV8j-M3qHzdzNLHlhbLq\">Critérios da Avaliação das Apresentações</a></p><p>&nbsp;</p><p>Link do edital acessível:</p><p><a href=\"https://youtu.be/klwPz_2OquM\">https://youtu.be/klwPz_2OquM</a><br>&nbsp;</p><p>Link do regulamento acessível:</p><p><a href=\"https://youtu.be/Dez15xEVjfs\">https://youtu.be/Dez15xEVjfs</a></p>', 'Edital 03/2019', '04-10-2019-16-00-31.pdf', NULL, 1),
(2, 'Mostra Artística dos IV Encontros Universitários da UFC de Russas', '<p>Além da programação tradicional com apresentações orais, palestras e minicursos, haverá também a primeira edição da Mostra Artística dos EU2019, com apresentações musicais, de dança e teatro, filmes, oficinas, exposições, feira de artesanato e gastronomia. As inscrições estarão abertas de 1º a 11 de outubro para estudantes, servidores docentes e técnico-administrativos que desejarem submeter propostas. O regulamento e ficha de inscrição estarão disponíveis no site dos Encontros.</p><p>&nbsp;</p><p><a href=\"https://forms.gle/f4TAz3Uj8mXBN7Gr6\">Ficha de inscrição&nbsp;</a></p>', 'Regulamento de Inscrição', '25-09-2019-09-29-53.pdf', NULL, 1),
(3, 'EDITAL 03/2020 – UFC CAMPUS DE RUSSAS', '<p>A Direção do Campus da Universidade Federal do Ceará de Russas, por intermédio da Comissão Organizadora do evento, torna pública a abertura de inscrições e estabelece normas relativas à participação no V ENCONTROS UNIVERSITÁRIOS DO CAMPUS DA UFC EM RUSSAS, que será realizado no período de 10 a 12 de março de 2021 em formato remoto.</p>', 'V ENCONTROS UNIVERSITÁRIOS', '19-01-2021-09-55-30.pdf', '0', 2),
(5, 'EDITAL 03/2020 – UFC CAMPUS DE RUSSAS - V ENCONTROS UNIVERSITÁRIOS', '<p>Vídeo do edital traduzido para a língua de sinais dos Encontros Universitários 2020 do Campus da UFC em Russas.</p>', 'Vídeo do edital traduzido para a língua de sinais.', NULL, 'https://www.youtube.com/watch?v=pXAdxCePJd4', 2),
